package BasicFlowControl;

public class Switch_Expression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day =3;
		String dayName ;
		
		switch(day) {
		case 1:
			dayName = "Monday";
			break;
		
		case 2:
			dayName = "Tuesday";
			break;
		
		case 3:
			dayName = "Wednesday";
			System.out.println("Wednesday");

			break;

	  	default:
	  		dayName = "Invalid";{
	  			
	  		  		System.out.println(" DAY :" + dayName);  	
	  		
		}
		
			
		
		
	}
	
}}