package BasicFlowControl;

public class if_else_Condition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -5;
		
		if(num > 0){
			System.out.println("Number Is Positive");
			}
			else {
		    System.out.println("Number Is Negative");
			}
			
		
		
	}

}
