package day01;

public class straightTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	 int k = 1;
		for (int i=0;i<5;i++)
		{
			for (int j=0;j<=i;j++)
			{
				System.out.print(k);
				System.out.print("  ");
				k++;
			}
			System.out.println("");
		}
			

	}

}
